new class{constructor(){this.cacheDom(),this.handleEvents()}cacheDom(){this.menuBtn=document.querySelector(".navigation__menu-btn"),this.menuList=document.querySelector(".navigation__list")}handleEvents(){this.menuBtn.addEventListener("click",t=>{const e="true"===this.menuBtn.getAttribute("aria-expanded");this.menuBtn.setAttribute("aria-expanded",!e),this.menuList.dataset.state=e?"closed":"open"})}};